import '../style/NonProfitsPage.css'
import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import DataTable from 'react-data-table-component';

const NonProfitsPage = () => {
    const API_URL_GET_ALL = 'http://localhost:8080/nonprofit/getAll';

    const [ nonProfitList, setNonProfitList ] = useState([]);

    const fetchNonProfitList = async () => {
        try {
            const response = await fetch(API_URL_GET_ALL);
            const info = await response.json();
            console.log(JSON.stringify(info));
            setNonProfitList(info);
        } catch (e) {
            console.error(e.stack)
        }
    }

    useEffect(() => {
        fetchNonProfitList();
    }, []);

    const tableColumns = [
        { name: 'ID', selector: row => row.id, sortable:true},
        { name: 'Name', selector: row => row.name, sortable:true},
        { name: 'Email', selector: row => row.email, sortable:true},
        { name: 'Address', selector: row => row.address, sortable:true},
        { name: 'Fund Transferred', selector: row => row.fundReceived, sortable:true}
    ]

    const handleSearch = (e) => {
        const newList = nonProfitList.filter((row) => row.name.toLowerCase().includes(e.target.value.toLowerCase()));
        tableData = newList;
    }

    var tableData = nonProfitList;

    return (
        <div className='non-profit-main'>
            <div className='non-profit-search'>
                Search
                <input type='text' onChange={handleSearch} />
            </div>
            <DataTable 
                columns={tableColumns}
                data={tableData}
                fixedHeader
                pagination
            >
            </DataTable>
        </div>
    );
}

export default NonProfitsPage;
